import React from "react"
import {connect} from "react-redux"
import {withRouter,Link} from "react-router-dom"


import * as projectActions from "../../redux/actions/projectActions"
import {bindActionCreators} from 'redux';
//font end
//import CoolGuy from '../../assets/img/cool_guy.png'
import {Modal,message,Badge} from 'antd'
import {

  Button,
  Card,
  Spinner,
  CardBody,
  CardHeader,
  CardImg,
  CardTitle,
  Col,
  Container,
  DropdownItem,
  DropdownMenu,
  DropdownToggle,
  ListGroup,
  ListGroupItem,
  Progress,
  Row,
  UncontrolledDropdown
} from "reactstrap";

import { MoreHorizontal } from "react-feather";

import avatar1 from "../../assets/img/avatars/avatar.jpg";
import avatar2 from "../../assets/img/avatars/avatar-2.jpg";
import avatar3 from "../../assets/img/avatars/avatar-3.jpg";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlus } from "@fortawesome/free-solid-svg-icons";

class Projects extends React.Component{
  constructor(props){
    super(props)
    this.state = {
      title: 'Projects',
      project: {
        project_title: "",
        project_desc: "",
        active: false
      },
      visible: false
    }
  }

  success = () => {
  Modal.success({
    content: (
    <div>
      <p>Check out our key</p>
      <p>some messages...some messages...</p>
    </div>
  ),
  });
}


  componentDidMount(){
    this.props.actions.projects(this.props.match.params.subdomain)
  }

  showModal = () => {
 this.setState({
   visible: true,
 });
};

handleOk = e => {
 console.log(e);
 this.setState({
   visible: false,
 });
};

handleCancel = e => {
 console.log(e);
 this.setState({
   visible: false,
 });
};

deleteThisProject = (id) => {
 this.props.deleteProject(id)
}

  render(){
    const Project = ({ name, state, color, percentage, description, key, uid }) => (
    <Card>
      <CardHeader className="px-4 pt-4">
        <div className="card-actions float-right">
          <UncontrolledDropdown>
            <DropdownToggle tag="a">
              <MoreHorizontal />
            </DropdownToggle>
            <DropdownMenu right>
              <DropdownItem><Link to={`/${props.account.subdomain}/projects/${uid}`}>Project Details</Link></DropdownItem>
              <DropdownItem onClick={() => this.deleteThisProject(uid)}>Delete Project</DropdownItem>
            </DropdownMenu>
          </UncontrolledDropdown>
        </div>
        <CardTitle tag="h5" className="mb-0">
          {name}
        </CardTitle>
        {/*
          <Badge className="my-2" color={color}>
          {state}
        </Badge>
        */}
      </CardHeader>
      <CardBody className="px-4 pt-2">
        <p>{description}</p>
        <Badge status="success" text="Active" />
      </CardBody>
      {/* put progress bar here */}
    </Card>
  );

    let display_projects
    switch(this.props.loaded_projects){
      case false:
      display_projects = <div>No</div>
      break
      case true:
      display_projects = this.props.projects.map(sd =>
        <Col md="6" lg="3">
          <Project
            name={sd.project_title}
            state="Finished"
            color="success"
            percentage="100"
            description={sd.project_desc}
            key={sd.id}
            uid={sd.id}
          />
        </Col>
      )
    }

    return(
      <Container fluid className="p-0">
        <h1 className="h3 mb-3">{this.state.title}</h1>
        <Row>
          {display_projects}
        </Row>
      </Container>
    )
  }
}

const mapStateToProps = (state, props) => ({
  account: state.user.account,
  projects: state.project.projects,
  loaded_projects: state.project.loaded_projects
});

function mapDispatchToProps(dispatch) {
  return {
    actions: bindActionCreators(projectActions, dispatch)
  };
}

export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(Projects)
);
