import React from "react";
import classnames from "classnames";

import {
  Col,
  Container,
  Nav,
  NavItem,
  NavLink,
  Row,
  TabContent,
  TabPane
} from "reactstrap";

import {Form,Item,Icon,Button} from 'antd'

import { Home, Settings, MessageSquare,Users,GitMerge } from "react-feather";


class PopoverNode extends React.Component {
  constructor(props) {
    super(props);

    this.toggle = this.toggle.bind(this);
    this.state = {
      activeTab: "1"
    };
  }

  toggle(tab) {
    if (this.state.activeTab !== tab) {
      this.setState({
        activeTab: tab
      });
    }
  }

  render() {
    const { name, className } = this.props;

    return (
      <div className={"tab " + className}>
        <Nav tabs>
          <NavItem>
            <NavLink
              className={classnames({ active: this.state.activeTab === "1" })}
              onClick={() => {
                this.toggle("1");
              }}
            >
              <Home />
            </NavLink>
          </NavItem>
          <NavItem>
            <NavLink
              className={classnames({ active: this.state.activeTab === "2" })}
              onClick={() => {
                this.toggle("2");
              }}
            >
              <Settings />
            </NavLink>
          </NavItem>
          <NavItem>
            <NavLink
              className={classnames({ active: this.state.activeTab === "3" })}
              onClick={() => {
                this.toggle("3");
              }}
            >
              <Users />
            </NavLink>
          </NavItem>
          <NavItem>
            <NavLink
              className={classnames({ active: this.state.activeTab === "4" })}
              onClick={() => {
                this.toggle("3");
              }}
            >
              <GitMerge/>
            </NavLink>
          </NavItem>
        </Nav>
        <TabContent activeTab={this.state.activeTab}>
          <TabPane tabId="1">
          <Form>
            <Form.Item>
              <Input
                name='task_title'
                value={props.task_details.task_title}
                onChange={props.newtaskChange(props.task_details.task_title)}
                placeholder="Task Title"
              />
            </Form.Item>
            <Form.Item>
            <TextArea

            name='task_desc'
            value={props.task_details.task_desc}
            onChange={props.newtaskChange(props.task_details.task_desc)}
            placeholder="Task Description"
            autoSize={{ minRows: 2, maxRows: 6 }}
          />
  <Divider dashed />
            </Form.Item>
            </Form>
          </TabPane>
          <TabPane tabId="2">

          </TabPane>
          <TabPane tabId="3">

          </TabPane>
        </TabContent>
      </div>
    );
  }
}

export default PopoverNode
