import React from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { withRouter,useParams } from "react-router-dom";
import {retrieveUserAccount} from '../redux/actions/userActions'

import Wrapper from "../components/Wrapper";
import Sidebar from "../components/Sidebar";
import Main from "../components/Main";
import Navbar from "../components/Navbar";
import Content from "../components/Content";
import Footer from "../components/Footer";
import Settings from "../components/Settings";

class Dashboard extends React.Component{
  constructor(props){
    super(props)
    this.state = {
      loggedIn: 'yes'
    }
    this.updateLogged = this.updateLogged.bind(this)
  }

  updateLogged = () => {
    console.log('fuckyou')
  }
  componentDidMount(){
    const { subdomain } = this.props.match.params
    this.props.retrieveUserAccount(subdomain)
  }

  processProps = (child) => {
    switch(child.type.name){
      case 'Hub':
      return {
        loggedIn: this.state.loggedIn,
        user: this.props.user,
        updateLogged: this.updateLogged
      }
    }
  }

  render(){

    const childrenWithProps = React.Children.map(this.props.children, child => {
      return React.cloneElement(child, this.processProps(child))
    })

    return(
      <React.Fragment>
        <Wrapper>
          <Sidebar />
          <Main>
            <Navbar user={this.props.user} />
            <Content>
            {childrenWithProps}
            </Content>
            <Footer />
          </Main>
        </Wrapper>
        <Settings />
      </React.Fragment>
    )
  }
}

const mapStateToProps = (state, props) => ({
  user: state.user.user,
  account: state.user.account
});

export default withRouter(
  connect(
    mapStateToProps,
    {retrieveUserAccount}
  )(Dashboard)
);
