import Auth from '../../auth'
class taskApi {

  static getProjectTasks(project_id){
    return fetch(`http://localhost:3001/api/v1/tasks?project_id=${project_id}`,{
      method: "GET",
      headers: Auth.fetchToken()
    })
    .then(response => response.json())
    .catch(error => {
      return error;
    })
  }

  static addTask(client_task_params){
    return fetch(`http://localhost:3001/api/v1/tasks?project_id=${client_task_params[0].id}&id=${client_task_params[1]}&type=${client_task_params[2]}`,{
      method: "POST",
      headers: Auth.fetchToken(),
      headers: {
      'content-type': 'application/json'
    },
      body: JSON.stringify(client_task_params[3])
    }).then(response => {
      return response.json()
    }).catch(error => {
      return error;
    })
  }
}

export default taskApi
