import React from 'react'
import {connect} from 'react-redux'
import {withRouter} from 'react-router-dom'
import * as userActions from "../../redux/actions/userActions"
import {bindActionCreators} from 'redux';

import {
  Button,
  Form,
  Input,
  Icon,
  Item
} from 'antd'

class TeamSignUp extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      team: '',
      invitation: '',
      email: '',
      password: '',
      origin: 'invite'
    }
    this.teamSignUpChange = this.teamSignUpChange.bind(this)
  }

  componentDidMount(){
    this.setState({
      team: this.props.match.params.team,
      invitation: this.props.match.params.invitation
    })
  }

  teamSignUpChange = input => e => {
    const name = e.target.name
    this.setState({
      [name]: e.target.value
    })
  }

  signUpAndAccept = (e) => {
    e.preventDefault()
    const userParams = {
      email: this.state.email,
      password: this.state.password,
      origin: this.state.invite
    }
    const sub_domain = this.state.team
    this.props.actions.teamSignUp(userParams, sub_domain)
  }

  render(){
    return(
      <div>Welcome To {this.state.team} Sign Up
      <Form>
        <Form.Item>
          <Input
          name='email'
          value={this.state.email}
          onChange={this.teamSignUpChange(this.state.email)}
          />
        </Form.Item>
        <Form.Item>
          <Input
            name='password'
            value={this.state.password}
            onChange={this.teamSignUpChange(this.state.password)}
          />
        </Form.Item>
        <Form.Item>
          <Button onClick={this.signUpAndAccept}>Join the team</Button>
        </Form.Item>
      </Form>
      </div>
    )
  }
}

const mapStateToProps = (state, props) => ({
  user: state.user.user,
  account: state.user.account
});

function mapDispatchToProps(dispatch) {
  return {
    actions: bindActionCreators(userActions, dispatch)
  };
}

export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(TeamSignUp)
);
