  static retrieveProject(id,subdomain){
    return fetch(`http://localhost:3001/api/v1/projects/${id.id}?subdomain=${subdomain}`, {
      method: 'GET',
      headers: Auth.fetchToken()
    }).then(response => {
      return response.json()
