import React from "react"
import {connect} from "react-redux"
import {withRouter,Link} from "react-router-dom"
import * as projectActions from "../../redux/actions/projectActions"
import * as taskActions from "../../redux/actions/taskActions"
import {bindActionCreators} from 'redux';

import TaskDrawerAdd from './taskDrawerAdd'
import TaskDrawerDetails from './taskDrawerDetails'
import PopoverNode from './popoverNode'
import ProjectHeader from './projectHeader'
import ProjectStatistic from './projectStatistic'
import ProjectGram from './projectGram'
import ProjectGramBig from './projectGramBig'
import classnames from "classnames";
import {
  Badge,
  Card,
  CardBody,
  CardHeader,
  CardImg,
  CardTitle,
  Col,
  Container,
  DropdownItem,
  DropdownMenu,
  DropdownToggle,
  ListGroup,
  ListGroupItem,
  Row,
  UncontrolledDropdown,
  Nav,
  NavItem,
  NavLink,
  TabContent,
  TabPane
} from "reactstrap";

import { MoreHorizontal } from "react-feather";

import avatar1 from "../../assets/img/avatars/avatar.jpg";
import avatar2 from "../../assets/img/avatars/avatar-2.jpg";
import avatar3 from "../../assets/img/avatars/avatar-3.jpg";
import { Calendar, Filter, RefreshCw } from "react-feather";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlus } from "@fortawesome/free-solid-svg-icons";
import {
  Tooltip,
  Popover,
  Popconfirm,
  Message,
  Button,
  Drawer,
  Form,
  Input,
  Item,
  Icon,
  Menu,
  Progress
} from 'antd'
import { Group } from '@vx/group';
import { Tree } from '@vx/hierarchy';
import { LinearGradient } from '@vx/gradient';
import {Zoom} from '@vx/zoom'
import {localPoint} from '@vx/event'
import {scaleLinear} from '@vx/scale'
import { hierarchy } from 'd3-hierarchy';
import { pointRadial } from 'd3-shape';
import { RectClipPath } from '@vx/clip-path';
import {
  LinkHorizontal,
  LinkVertical,
  LinkRadial,
  LinkHorizontalStep,
  LinkVerticalStep,
  LinkRadialStep,
  LinkHorizontalCurve,
  LinkVerticalCurve,
  LinkRadialCurve,
  LinkHorizontalLine,
  LinkVerticalLine,
  LinkRadialLine
} from '@vx/shape';
//import Viewer from 'react-drag-n-zoom'


const {TextArea} = Input

class Project extends React.Component {
  constructor(){
    super()
    this.state = {
      activeTab: "1",
      layout: 'cartesian',
      orientation: 'horizontal',
      linkType: 'diagonal',
      stepPercent: 0.5,
      parent: '',
      type: '',
      project_id: '',
      task: {
        task_title: '',
        task_desc: '',
        status: 0,
        priority: 0,
        assigned_to: '',
        created_by: '',
        due_type: true,
      },
      new_task:false,
      initialTransform: {
        scaleX: 1.27,
        scaleY: 1.27,
        translateX: -211.62,
        translateY: 162.59,
        skewX: 0,
        skewY: 0
      }
    };
    this.retrieveTask = this.retrieveTask.bind(this)
    this.newtaskChange = this.newtaskChange.bind(this)
    this.newPriority = this.newPriority.bind(this)
    this.newStatus = this.newStatus.bind(this)
    this.changeTranslate = this.changeTranslate.bind(this)
    this.toggle = this.toggle.bind(this);
  }

  toggle(tab) {
    if (this.state.activeTab !== tab) {
      this.setState({
        activeTab: tab
      });
    }
  }

  componentDidMount() {
    console.log("called did mount")
    const project_id = this.props.match.params
    this.props.actions.retrieveProject(project_id,this.props.match.params.subdomain)
  }

  newPriority (value){
    const new_priority = parseInt(value,10)
    this.setState({
      task: {
        ...this.state.task,
        priority: new_priority
      }
    })

  }

  newStatus (value){
    const new_status = parseInt(value,10)
    this.setState({
      task: {
        ...this.state.task,
        status: new_status
      }
    })

  }

  newDueType = (e) => {
    let new_value
    if (e.target.value == 'asap') {
      new_value = true
    }else {
      new_value = false
    }
    this.setState({
      task: {
        ...this.state.task,
        due_type: new_value
      }
    })
  }

  newtaskChange = input => e => {
    const name = e.target.name
    this.setState({
      task: {
        ...this.state.task,
        [name]: e.target.value
      }
    })
    console.log(this.state.task)
  }

  showDrawer = () => {
    this.setState({
      visible: true
    })
  }

  onClose = () => {
    this.setState({
      visible: false,
      new_task: false
    })
  }

  _deleteTaskFromParent = (task) => {
    this.props.actions.deleteTaskFromParent(task)
  }

  _addTaskToParent = (node,type) => {

    let client_task_params = [
      this.props.match.params,
      node.data.id,
      type,
      this.state.task
    ]
    this.props.actions.addTaskToParent(client_task_params, this.props.account.subdomain)
  }

/*
  _addTaskToParent = (e) => {
    e.preventDefault()
    let client_task_params = [
      this.props.match.params,
      this.state.parent,
      this.state.type,
      this.state.task
    ]
    this.props.actions.addTaskToParent(client_task_params, this.props.account.subdomain)
    this.setState({
      task: {
        task_title: '',
        task_desc: ''
      }
    })
  }
*/
  retrieveTask = (id,action,type) => {
    const api_type = type
    if(action == 'details'){
      this.setState({
        task: id.data
      })
    }else {
      this.setState({
        parent: id.data.id,
        new_task: true,
        type: api_type
      })
    }
    //this.showDrawer()
  }

  changeTranslate = (e,zoom,node) => {
