import Auth from '../../auth'
import {currEnv} from './apiConfig'

const api = `api/v1`

class projectApi {


  static genProjectConfig (resource, param_line, type){

    genProjHeaders = (type) => {
      switch(type){
        case 'GET':
        return (`${currEnv}/${api}/${resource}/${param_line}`,{
          method: 'GET',
          headers: Auth.fetchToken()
        })

      }
    }

  }

  static getProjects(subdomain){
    this.genProjectConfig('projects',`?subdomain=${subdomain}`,'GET')
    .then(response => response.json())
    .catch(error => {
      return error;
    })
  }

  static createNewProject(project,subdomain){
    return fetch(`http://localhost:3001/api/v1/projects?subdomain=${subdomain}`,{
      method: 'POST',
      headers: Auth.fetchToken(),
      body: JSON.stringify(project)
    }).then(response => {
      return response.json();
    }).catch(error => {
      return error;
    });
  }

  static deleteProject(id){
    return fetch(`http://localhost:3001/api/v1/projects/${id}`, {
      method: 'DELETE',
      headers: Auth.fetchToken()
    }).then(response => {
      return response.json()
    }).catch(error => {
      return error;
    })
  }

  static retrieveProject(id,subdomain){
    return fetch(`http://localhost:3001/api/v1/projects/${id.id}?subdomain=${subdomain}`, {
      method: 'GET',
      headers: Auth.fetchToken()
    }).then(response => {
      return response.json()
    }).catch(error => {
      return error;
    })
  }

}

export default projectApi
