import * as types from "../constants";
import taskApi from '../api/taskApi'

export function projectTasks(project_id,subdomain){
  return function(dispatch){
    return taskApi.getProjectTasks(project_id,subdomain).then(tasks => {
      dispatch({
        type: types.GET_PROJECT_TASKS,
        payload: tasks
      })
    }).catch(error => {
      throw(error)
    })
  }
}

export function addTaskToParent(client_plan_params,subdomain){
  return function(dispatch){
    taskApi.addTask(client_plan_params,subdomain).then(plan => {
      dispatch({
        type: types.ADD_TASK,
        payload: plan
      })
    }).catch(error => {
      throw(error)
    })
  }
}

