import * as types from "../constants";
import projectApi from '../api/projectApi'

export function projects(){
  return function(dispatch){
    return projectApi.getProjects().then(project =>{
      dispatch({
        type: types.GET_PROJECTS,
        payload: project
      })
    }).catch(error => {
      throw(error)
    })
  }
}

export function createProject(project){
  return function(dispatch){
    return projectApi.createNewProject(project).then(responseProject => {
      dispatch({
        type: types.CREATE_PROJECT,
        payload: responseProject
      })
    }).catch(error => {
      throw(error)
    })
  }
}

export function deleteProject(project){
  return function(dispatch){
    return projectApi.deleteProject(project).then(responseProject => {
      dispatch({
        type: types.DELETE_PROJECT,
        payload: responseProject
      })
    }).catch(error => {
      throw(error)
    })
  }
}

export function retrieveProject(id){
  return function(dispatch){
    return projectApi.retrieveProject(id).then(responseProject => {
      dispatch({
        type: types.PROJECT_DETAIL,
        payload: responseProject
      })
    })
    .catch(error => {
      throw(error)
    })
  }
}
