import React from 'react'
import {connect} from 'react-redux'
import {withRouter} from 'react-router-dom'
import * as userActions from "../../redux/actions/userActions"
import {bindActionCreators} from 'redux';

class TeamSignUp extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      team: '',
      invitation: '',
      email: '',
      password: '',
      origin: 'invite'
    }
    this.teamSignUpChange = this.teamSignUpChange.bind(this)
  }

  componentDidMount(){
    this.setState({
      team: this.props.match.params.team,
      invitation: this.props.match.params.invitation
    })
  }

  teamSignUpChange = input => e => {
    const name = e.target.name
    this.setState({
      [name]: e.target.value
    })
  }

  signUpAndAccept(){
    this.props.actions.teamSignUp(this.state)
  }

  render(){
    return(
      <div>Welcome To {this.state.team} Sign Up
      <Form>
        <Form.Item>
          <Input
          name='email'
          value={this.state.email}
          onChange={this.teamSignUpChange(this.state.email)}
          />
        </Form.Item>
      </Form>
      </div>
    )
  }
}

const mapStateToProps = (state, props) => ({
  user: state.user.user,
  account: state.user.account
});

function mapDispatchToProps(dispatch) {
  return {
    actions: bindActionCreators(userActions, dispatch)
  };
}

export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(TeamSignUp)
);
