import React from "react"
import {connect} from "react-redux"
import {withRouter,Link} from "react-router-dom"
import * as projectActions from "../../redux/actions/projectActions"
import * as taskActions from "../../redux/actions/taskActions"
import {bindActionCreators} from 'redux';

import TaskDrawerAdd from './taskDrawerAdd'
import TaskDrawerDetails from './taskDrawerDetails'
import PopoverNode from './popoverNode'

import {
  Badge,
  Card,
  CardBody,
  CardHeader,
  CardImg,
  CardTitle,
  Col,
  Container,
  DropdownItem,
  DropdownMenu,
  DropdownToggle,
  ListGroup,
  ListGroupItem,
  Row,
  UncontrolledDropdown
} from "reactstrap";

import { MoreHorizontal } from "react-feather";

import avatar1 from "../../assets/img/avatars/avatar.jpg";
import avatar2 from "../../assets/img/avatars/avatar-2.jpg";
import avatar3 from "../../assets/img/avatars/avatar-3.jpg";
import { Calendar, Filter, RefreshCw } from "react-feather";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlus } from "@fortawesome/free-solid-svg-icons";
import {
  Tooltip,
  Popover,
  Popconfirm,
  Message,
  Button,
  Drawer,
  Form,
  Input,
  Item,
  Icon,
  Menu,
  Progress
} from 'antd'
import { Group } from '@vx/group';
import { Tree } from '@vx/hierarchy';
import { LinearGradient } from '@vx/gradient';
import {Zoom} from '@vx/zoom'
import {localPoint} from '@vx/event'
import {scaleLinear} from '@vx/scale'
import { hierarchy } from 'd3-hierarchy';
import { pointRadial } from 'd3-shape';
import { RectClipPath } from '@vx/clip-path';
import {
  LinkHorizontal,
  LinkVertical,
  LinkRadial,
  LinkHorizontalStep,
  LinkVerticalStep,
  LinkRadialStep,
  LinkHorizontalCurve,
  LinkVerticalCurve,
  LinkRadialCurve,
  LinkHorizontalLine,
  LinkVerticalLine,
  LinkRadialLine
} from '@vx/shape';
//import Viewer from 'react-drag-n-zoom'


const {TextArea} = Input

class Project extends React.Component {
  constructor(){
    super()
    this.state = {
      layout: 'cartesian',
      orientation: 'horizontal',
      linkType: 'diagonal',
      stepPercent: 0.5,
      parent: '',
      type: '',
      project_id: '',
      task: {
        task_title: '',
        task_desc: '',
        status: 0,
        priority: 0
      },
      new_task:false,
    };
    this.retrieveTask = this.retrieveTask.bind(this)
    this.newtaskChange = this.newtaskChange.bind(this)
    this.newSelectChange = this.newSelectChange.bind(this)
  }

  componentDidMount() {
    const project_id = this.props.match.params
    this.props.actions.retrieveProject(project_id,this.props.match.params.subdomain)
  }

  newSelectChange = value => e => {
    console.log(e)
  }
  newtaskChange = input => e => {
    console.log(input)
    console.log(e)
  }

  showDrawer = () => {
    this.setState({
      visible: true
    })
  }

  onClose = () => {
    this.setState({
      visible: false,
      new_task: false
    })
  }

  _deleteTaskFromParent = (task) => {
    this.props.actions.deleteTaskFromParent(task)
  }

  _addTaskToParent = (node,type) => {

    let client_task_params = [
      this.props.match.params,
      node.data.id,
      type,
      this.state.task
    ]
    this.props.actions.addTaskToParent(client_task_params, this.props.account.subdomain)
  }

/*
  _addTaskToParent = (e) => {
    e.preventDefault()
    let client_task_params = [
      this.props.match.params,
      this.state.parent,
      this.state.type,
      this.state.task
    ]
    this.props.actions.addTaskToParent(client_task_params, this.props.account.subdomain)
    this.setState({
      task: {
        task_title: '',
        task_desc: ''
      }
    })
  }
*/
  retrieveTask = (id,action,type) => {
    const api_type = type
    if(action == 'details'){
      this.setState({
        task: id.data
      })
    }else {
      this.setState({
        parent: id.data.id,
        new_task: true,
        type: api_type
      })
    }
    //this.showDrawer()
  }

  render() {
    const initialTransform = {
  scaleX: 1.27,
  scaleY: 1.27,
  translateX: -211.62,
  translateY: 162.59,
  skewX: 0,
  skewY: 0
};
    const {
      width = 1000,
      height = 500,
      margin = {
        top: 20,
        left: 90,
        right: 30,
        bottom: 90
      }
    } = this.props;

    if(this.props.project_status && !this.props.fetched_tasks){
      this.props.actions.projectTasks(this.props.project.id,this.props.account.subdomain)
    }

    const data = this.props.tasks

    const { layout, orientation, linkType, stepPercent } = this.state;

    const innerWidth = width - margin.left - margin.right;
    const innerHeight = height - margin.top - margin.bottom;

    let origin;
    let sizeWidth;
    let sizeHeight;

    let task_drawer
    switch(this.state.new_task){
      case false:
        task_drawer = <TaskDrawerDetails task_details={this.state.task} />
        break;
      case true:
        task_drawer = <TaskDrawerAdd
                        task_details={this.state.task}
                        newtaskChange={this.newtaskChange}
                        addTaskToParent={this._addTaskToParent}
                      />
    }

    if (layout === 'polar') {
      origin = {
        x: innerWidth / 2,
        y: innerHeight / 2
      };
      sizeWidth = 2 * Math.PI;
      sizeHeight = Math.min(innerWidth, innerHeight) / 2;
    } else {
      origin = { x: 0, y: 0 };
      if (orientation === 'vertical') {
        sizeWidth = innerWidth;
        sizeHeight = innerHeight;
      } else {
        sizeWidth = innerHeight;
        sizeHeight = innerWidth;
      }
    }

    return (
      <div>

      <Drawer
        title={this.state.task.task_title}
        placement="right"
        closable={false}
        width={400}
        onClose={this.onClose}
        visible={this.state.visible}
        >
        {task_drawer}
      </Drawer>



      <Row className="mb-2 mb-xl-4">
      <Col xs="auto" className="d-none d-sm-block">
        <h3>{this.props.project.project_title}</h3>
      </Col>


      <Col xs="auto" className="ml-auto text-right mt-n1">

      <Popover placement="bottom" title="asd" content="asd" trigger="click">
      <Button color="primary" className="shadow-sm mr-1 atn-btn-project">
        <Link to={`/${this.props.account.subdomain}/projects`}>Project Hub</Link>
      </Button>
      </Popover>
      <Button color="primary" className="shadow-sm mr-1 atn-btn-project">
        <RefreshCw className="feather" />
      </Button>

      {/*  <UncontrolledDropdown className="d-inline mr-2">
          <DropdownToggle caret color="light" className="bg-white shadow-sm">
            <Calendar className="feather align-middle mt-n1" /> Today
          </DropdownToggle>
          <DropdownMenu right>
            <DropdownItem>Action</DropdownItem>
            <DropdownItem>Another Action</DropdownItem>
            <DropdownItem>Something else here</DropdownItem>
            <DropdownItem divider />
            <DropdownItem>Seperated link</DropdownItem>
          </DropdownMenu>
        </UncontrolledDropdown>*/}

      </Col>
    </Row>

    <Zoom
      width={width}
      height={height}
      scaleXMin={1 / 2}
      scaleXMax={4}
      scaleYMin={1 / 2}
      scaleYMax={4}
      transformMatrix={{
  scaleX: 1.27,
  scaleY: 1.27,
  translateX: -211.62,
  translateY: 162.59,
  skewX: 0,
  skewY: 0
}}
    >
    {zoom => {
      return(
        <svg width={width} height={height} style={{ cursor: zoom.isDragging ? 'grabbing' : 'grab' }}>
          <RectClipPath id="zoom-clip" width={800} height={800} />
          <LinearGradient  id="lg" from="#fd9b93" to="#fe6e9e" />
          <rect onWheel={zoom.handleWheel}
                  onMouseDown={zoom.dragStart}
                  onMouseMove={zoom.dragMove}
                  onMouseUp={zoom.dragEnd}
                  onMouseLeave={() => {
                    if (!zoom.isDragging) return;
                    zoom.dragEnd();
                  }} width={width} height={height} rx={14} fill="#f6f9fc" />

          <Group transform={zoom.toString()} top={margin.top} left={margin.left}>
            <Tree
              root={hierarchy(data, d => (d.isExpanded ? null : d.children))}
              size={[sizeWidth, sizeHeight]}
              separation={(a, b) => (a.parent == b.parent ? 1 : 0.5) / a.depth}

            >
              {data => (
                <Group top={origin.y} left={origin.x}>
                  {data.links().map((link, i) => {
                    let LinkComponent;

                    if (layout === 'polar') {
                      if (linkType === 'step') {
                        LinkComponent = LinkRadialStep;
                      } else if (linkType === 'curve') {
                        LinkComponent = LinkRadialCurve;
                      } else if (linkType === 'line') {
                        LinkComponent = LinkRadialLine;
                      } else {
                        LinkComponent = LinkRadial;
                      }
                    } else {
                      if (orientation === 'vertical') {
                        if (linkType === 'step') {
                          LinkComponent = LinkVerticalStep;
                        } else if (linkType === 'curve') {
                          LinkComponent = LinkVerticalCurve;
                        } else if (linkType === 'line') {
                          LinkComponent = LinkVerticalLine;
                        } else {
                          LinkComponent = LinkVertical;
                        }
                      } else {
                        if (linkType === 'step') {
                          LinkComponent = LinkHorizontalStep;
                        } else if (linkType === 'curve') {
                          LinkComponent = LinkHorizontalCurve;
                        } else if (linkType === 'line') {
                          LinkComponent = LinkHorizontalLine;
                        } else {
                          LinkComponent = LinkHorizontal;
                        }
                      }
                    }

                    return (
                      <LinkComponent
                        data={link}
                        percent={+stepPercent}
                        stroke={link.target.children ? '#153d8f' : '#eee'}
                        strokeWidth="1"
                        fill="none"
                        key={i}
                        onClick={() => console.log(link)}
                      />
                    );
                  })}

                  {data.descendants().map((node, key) => {
                    const width = 120;
                    const height = 40;

                    let top;
                    let left;
                    if (layout === 'polar') {
                      const [radialX, radialY] = pointRadial(node.x, node.y);
                      top = radialY;
                      left = radialX;
                    } else {
                      if (orientation === 'vertical') {
                        top = node.y;
                        left = node.x;
                      } else {
                        top = node.x;
                        left = node.y;
                      }
                    }

                    return (

                      <Group top={top} left={left} key={key}>
                        {node.depth === 0 && (
                          <Popover placement="right" content={(<div>

                            <Button type="primary" icon="file" size='small' onClick={()=> this.retrieveTask(node,'details','root')}/>
                            <Button type="primary" icon="arrows-alt" size='small' onClick={() => {
                              node.data.isExpanded = !node.data.isExpanded;
                              console.log(node)
                              this.forceUpdate();
                            }}/>
                          <Button type="primary" icon="plus" size='small' onClick={()=> this.retrieveTask(node,'add','root')}/>
                          <p>storage used</p>
                          <Progress percent={0} size="small" />
                          </div>)} trigger="click">
                          <circle
                            r={15}
                            fill="#fbd24b"

                          />
                          </Popover>
                        )}
                        {node.depth !== 0 && (
                          <Popover content={(<div>
                            <PopoverNode
                              name="Vertical icon"
                              className="tab-vertical"
                              retrieveTask={this.retrieveTask}
                              node={node}
                              update={this.forceUpdate}
                              deleteTask={this._deleteTaskFromParent}
                              task_details={this.state.task}
                              newtaskChange={this.newtaskChange}
                              newSelectChange={this.newSelectChange}
                              addTaskToParent={this._addTaskToParent}

                            />
                          </div>)} trigger="click">
                          <rect
                            height={height}
                            width={width}
                            y={-height / 2}
                            x={-width / 2}
                            fill={'#fafafa'}
                            stroke={node.data.children.length < 1 ? '#153d8f' : '#153d8f'}
                            strokeWidth={1}
                            strokeDasharray={node.data.children.length < 1 ? '2,2' : '0'}
                            strokeOpacity={node.data.children.length < 1 ? 0.6 : 1}
                            rx={!node.data.children.length < 1 ? 4 : 10}

                          />
                          </Popover>
                        )}
                        <text
                          dy={'.33em'}
                          fontSize={9}
                          fontFamily="FontAwesome"
                          textAnchor={'middle'}
                          style={{ pointerEvents: 'none' }}
                          fill={node.depth === 0 ? '#71248e' : node.children ? 'white' : '#26deb0'}
                        >
                          This is a long title
                        </text>
                      </Group>

                    );
                  })}
                </Group>

              )}
            </Tree>
          </Group>
        </svg>
      )
    }}

        </Zoom>
      </div>
    );
  }
}


