import React from "react";
import { Line } from "react-chartjs-2";
import { connect } from "react-redux";
import PopoverNode from './popoverNode'
import { Badge, Card, CardBody, CardHeader, CardTitle } from "reactstrap";
import {
  Tooltip,
  Popover,
  Popconfirm,
  Message,
  Button,
  Drawer,
  Form,
  Input,
  Item,
  Icon,
  Menu,
  Progress
} from 'antd'
import { Group } from '@vx/group';
import { Tree } from '@vx/hierarchy';
import { LinearGradient } from '@vx/gradient';
import {Zoom} from '@vx/zoom'
import {localPoint} from '@vx/event'
import {scaleLinear} from '@vx/scale'
import { hierarchy } from 'd3-hierarchy';
import { pointRadial } from 'd3-shape';
import { RectClipPath } from '@vx/clip-path';
import {
  LinkHorizontal,
  LinkVertical,
  LinkRadial,
  LinkHorizontalStep,
  LinkVerticalStep,
  LinkRadialStep,
  LinkHorizontalCurve,
  LinkVerticalCurve,
  LinkRadialCurve,
  LinkHorizontalLine,
  LinkVerticalLine,
  LinkRadialLine
} from '@vx/shape';

const ProjectGramBig = ({
  changeTranslate,
  retrieveTask,
  deleteTask,
  newStatus,
  task_details,
  newtaskChange,
  newPriority,
  addTaskToParent,
  project,
  account_users,
  project_team_members,
  forceUpdate,
  data,
  state, props, theme }) => {
  console.log(data)
  console.log(account_users)
  const {
    width = 1000,
    height = 600,
    margin = {
      top: 10,
      left: 10,
      right: 10,
      bottom: 10
    }
  } = props;
  const innerWidth = width - margin.left - margin.right;
  const innerHeight = height - margin.top - margin.bottom;
  const { layout, orientation, linkType, stepPercent } = state;
  let origin;
  let sizeWidth;
  let sizeHeight;
  if (state.layout === 'polar') {
    origin = {
      x: innerWidth / 2,
      y: innerHeight / 2
    };
    sizeWidth = 2 * Math.PI;
    sizeHeight = Math.min(innerWidth, innerHeight) / 2;
  } else {
    origin = { x: 0, y: 0 };
    if (orientation === 'vertical') {
      sizeWidth = innerWidth;
      sizeHeight = innerHeight;
    } else {
      sizeWidth = innerHeight;
      sizeHeight = innerWidth;
    }
  }

  return (
    <div>
        <Zoom
          width={width}
          height={height}
          scaleXMin={1 / 2}
          scaleXMax={4}
          scaleYMin={1 / 2}
          scaleYMax={4}
          transformMatrix={data.children > 0 ? state.initialTransform : {scaleX: 1.27,
          scaleY: 1.27,
          translateX: width / 2,
          translateY: 0,
          skewX: 0,
          skewY: 0}}
        >
        {zoom => {
          return(
            <svg width={width} height={height} style={{ cursor: zoom.isDragging ? 'grabbing' : 'grab' }}>
              <RectClipPath id="zoom-clip" width={800} height={800} />
              <LinearGradient  id="lg" from="#fd9b93" to="#fe6e9e" />\

              <rect onWheel={zoom.handleWheel}
                      onMouseDown={zoom.dragStart}
                      onMouseMove={zoom.dragMove}
                      onMouseUp={zoom.dragEnd}
                      onMouseLeave={() => {
                        if (!zoom.isDragging) return;
                        zoom.dragEnd();
                      }} width={width} height={height} rx={14} fill="#fafafa" />

              <Group transform={zoom.toString()} top={margin.top} left={margin.left}>
                <Tree
                  root={hierarchy(data, d => (d.isExpanded ? null : d.children))}
                  size={[sizeWidth, sizeHeight]}
                  separation={(a, b) => (a.parent == b.parent ? 1 : 0.5) / a.depth}

                >
                  {data => (
                    <Group top={origin.y} left={origin.x}>
                      {data.links().map((link, i) => {
                        let LinkComponent;

                        if (layout === 'polar') {
                          if (linkType === 'step') {
                            LinkComponent = LinkRadialStep;
                          } else if (linkType === 'curve') {
                            LinkComponent = LinkRadialCurve;
                          } else if (linkType === 'line') {
                            LinkComponent = LinkRadialLine;
                          } else {
                            LinkComponent = LinkRadial;
                          }
                        } else {
                          if (orientation === 'vertical') {
                            if (linkType === 'step') {
                              LinkComponent = LinkVerticalStep;
                            } else if (linkType === 'curve') {
                              LinkComponent = LinkVerticalCurve;
                            } else if (linkType === 'line') {
                              LinkComponent = LinkVerticalLine;
                            } else {
                              LinkComponent = LinkVertical;
                            }
                          } else {
                            if (linkType === 'step') {
                              LinkComponent = LinkHorizontalStep;
                            } else if (linkType === 'curve') {
                              LinkComponent = LinkHorizontalCurve;
                            } else if (linkType === 'line') {
                              LinkComponent = LinkHorizontalLine;
                            } else {
                              LinkComponent = LinkHorizontal;
                            }
                          }
                        }

                        return (
                          <LinkComponent
                            data={link}
                            percent={+stepPercent}
                            stroke={link.target.children ? '#f14603' : '#f14603'}
                            strokeWidth="1"
                            fill="none"
                            key={i}
                            onClick={() => console.log(link)}
                          />
                        );
                      })}

                      {data.descendants().map((node, key) => {
                        const width = 240;
                        const height = 80;

                        let top;
                        let left;
                        if (layout === 'polar') {
                          const [radialX, radialY] = pointRadial(node.x, node.y);
                          top = radialY;
                          left = radialX;
                        } else {
                          if (orientation === 'vertical') {
                            top = node.y;
                            left = node.x;
                          } else {
                            top = node.x;
                            left = node.y;
                          }
                        }

                        return (

                          <Group top={top} left={left} key={key}>
                            {node.depth === 0 && (
                                <Popover placement="rightTop" content={(<div>
                                  <PopoverNode
                                    name="Vertical icon"
                                    className="tab-vertical"
                                    retrieveTask={retrieveTask}
                                    node={node}
                                    update={forceUpdate}
                                    deleteTask={deleteTask}
                                    task_details={task_details}
                                    newtaskChange={newtaskChange}
                                    newPriority={newPriority}
                                    newStatus={newStatus}
                                    addTaskToParent={addTaskToParent}
                                    project={project}
                                    account_users={account_users}
                                    project_team_members={project_team_members}
                                    type_id='root'

                                  />
                                  </div>)} trigger="click">
                              <circle
                                r={15}
                                fill="#060125"

                              />
                              </Popover>
                            )}
                            {node.depth !== 0 && (
                              <Popover placement="rightTop" content={(<div>
                                <PopoverNode
                                  name="Vertical icon"
                                  className="tab-vertical"
                                  retrieveTask={retrieveTask}
                                  node={node}
                                  update={forceUpdate}
                                  deleteTask={deleteTask}
                                  task_details={task_details}
                                  newtaskChange={newtaskChange}
                                  newPriority={newPriority}
                                  newStatus={newStatus}
                                  addTaskToParent={addTaskToParent}
                                  project={project}
                                  account_users={account_users}
                                  project_team_members={project_team_members}
                                  type_id='sub'

                                />
                              </div>)} trigger="click">
                              <rect
                                height={height}
                                width={width}
                                y={-height / 2}
                                x={-width / 2}
                                fill={'#fffdf4'}
                                stroke={node.data.children.length < 1 ? '#fffdf4' : '#fffdf4'}
                                strokeWidth={1}
                                strokeDasharray={node.data.children.length < 1 ? '2,2' : '0'}
                                strokeOpacity={node.data.children.length < 1 ? 0.6 : 1}
                                rx={!node.data.children.length < 1 ? 4 : 10}


                              />
                              </Popover>
                            )}
                            <text
                              dy={'.33em'}
                              fontSize={20}
                              fontFamily="FontAwesome"
                              textAnchor={'middle'}
                              style={{ pointerEvents: 'none' }}
                              fill={node.depth === 0 ? 'white' : node.children ? 'white' : 'white'}
                            >
                              {node.data.task_title}
                            </text>
                          </Group>

                        );
                      })}
                    </Group>

                  )}
                </Tree>
              </Group>
            </svg>
          )
        }}

            </Zoom>
        </div>

  );
};

export default connect(store => ({
  theme: store.theme.currentTheme
}))(ProjectGramBig);
