import Auth from '../../auth'
class taskApi {

  static getProjectTasks(project_id,subdomain){
    return fetch(`http://localhost:3001/api/v1/tasks?project_id=${project_id}&subdomain=${subdomain}`,{
      method: "GET",
      headers: Auth.fetchToken()
    })
    .then(response => response.json())
    .catch(error => {
      return error;
    })
  }

  static addTask(client_task_params,subdomain){
    return fetch(`http://localhost:3001/api/v1/tasks?project_id=${client_task_params[0].id}&id=${client_task_params[1]}&type=${client_task_params[2]}&subdomain=${subdomain}`,{
      method: "POST",
      headers: Auth.fetchToken(),
      body: JSON.stringify(client_task_params[3])
    }).then(response => {
      return response.json()
    }).catch(error => {
      return error;
    })
  }

  static deleteTask(task){
    return fetch(`http://localhost:3001/api/v1/tasks/${task.data.id}`, {
      method: "DESTROY",
      headers: Auth.fetchToken()
    }).then(response => {
      return response.json()
    }).catch(error => {
      return error;
    })
  }
}

export default taskApi
