import * as types from "../constants";
import Auth from "../../auth"

const initialState = {
  user: {},
  account: {},
  pending: false,
  logged_in: !!Auth.isUserAuthenticated()
};


export default function reducer(state = initialState, action) {
  switch (action.type) {
    case types.SIGN_UP_PENDING:
    return {
      ...state,
      pending: true
    }
    break;
    case types.SIGN_UP:
    Auth.processHead(action,action.type)
      return {
        ...state,
        user: action.payload.json.user,
        account: action.payload.json.account,
        pending: false,
        logged_in: !!Auth.isUserAuthenticated()
      }
    break;
    case types.SIGN_UP_ERROR:
    return {
      ...state,
      pending: false,
      error: action.error
    }
    break;
    case types.SIGN_OUT:
    Auth.processHead(action,action.type)
    return {
      user: {},
      account: {},
      logged_in: !!Auth.isUserAuthenticated()
    }
    break;
    case types.SIGN_IN:
    Auth.processHead(action,action.type)
    return{
      ...state,
      user: action.payload.json.user,
      account: action.payload.json.account,
      logged_in: !!Auth.isUserAuthenticated()
    }
    break;
    case types.RETRIEVE_USER_ACCOUNT:
      return {
        ...state,
        user: action.payload.user,
        account: action.payload.account
      }
    break;
    case types.SEND_INVITE:
      return {
        ...state,
        invite_status: 'invite sent'
      }
    break;
    default:
      return state;
  }
}
