//import SeedApi from './seedapi'
import Auth from "../../auth"
class userApi {

  static signupUser(user){
    return fetch('http://app.lvh.me:3001/api/v1/users',{
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({user: user})
    }).then(res => res.json().then(json => ({
      headers: res.headers,
      status: res.status,
      json
    })))
    .then(json => {
      return json
    })
    .catch(error => {
      return error;
    })
  }

  static retrieveAccount(subdomain){
    return fetch(`http://app.lvh.me:3001/api/v1/user/accounts/current_account?subdomain=${subdomain}`,{
      method: "GET",
      headers: Auth.fetchToken()
    })
    .then(response => response.json())
    .catch(error => {
      return error;
    })
  }

  static signInUser(user){
    return fetch('http://localhost:3001/api/v1/users/sign_in',{
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({user: user})
    }).then(res => res.json().then(json => ({
      headers: res.headers,
      status: res.status,
      json
    })))
    .then(json => {
      return json
    })
    .catch(error => {
      return error;
    })
  }

}

export default userApi
