import React from "react";
import { Link } from "react-router-dom";
import AOS from 'aos'
import 'aos/dist/aos.css';
import {
  Button,
  Card,
  CardBody,
} from "reactstrap";
import {
  Form,
  Icon,
  Input,
  Item,
  notification
} from "antd"
import Illu from "../../assets/img/photos/illustration-3.png";

import CreateSubdomain from "./createSubdomain"
import CreateEmailPassword from "./createEmailPassword"

class SignUp extends React.Component {
  constructor(){
    super()
    this.state ={
      approved_subdomain: false,
      subdomain: '',
      email: '',
      password: ''
    }
    this.signUpChange = this.signUpChange.bind(this)
    this.nextStep = this.nextStep.bind(this)
  }

  signUpChange = input => e => {
    const name = e.target.name
    this.setState({
      [name]: e.target.value
    })
    console.log(this.state.subdomain)
  }

  nextStep = (e) => {
    e.preventDefault()
    this.setState({
      approved_subdomain: true
    })
    notification['success']({
    message: 'Nice Job',
    description:
      `Your hub name is how you access your account! So it would be ${this.state.subdomain}`
  });
  }

  render(){
    let sign_up_step
    switch(this.state.approved_subdomain){
      case false:
        sign_up_step = <CreateSubdomain signUpChange={this.signUpChange}  subdomain={this.state.subdomain} nextStep={this.nextStep} />
      break;
      case true:
        sign_up_step = <CreateEmailPassword signUpChange={this.signUpChange} email={this.state.email} password={this.state.password} />
    }
    AOS.init()
    return(
      <section class="section-border border-primary">
      <div class="container d-flex flex-column">
        <div class="row align-items-center justify-content-center no-gutters min-vh-100">
          {sign_up_step}
        </div>
      </div>
    </section>
    )
  }
}


export default SignUp;
